) ENGINE=InnoDB AUTO_INCREMENT=22 DEFAULT CHARSET=utf8 COMMENT='视频信息';

-- ----------------------------
-- Records of video_drama_info
-- ----------------------------
INSERT INTO `video_drama_info` VALUES ('1', '9393904', '4289454', '拜见宫主大人第一季第1集', '秦斩是天龙八部游戏中一位非常有实力的资深玩家。擅闯灵鹫宫，要与虚竹大战一场，就是为了提升自己的实力。秦斩解决掉了虚竹大神，高傲的离开。游戏中的开封城内，秦斩的实力众人都已得知。正当众人还在谈论秦斩的时候，他却已经走进了开封城内。众多游戏玩家看见了秦斩来到此地，纷纷落荒而逃，场面一片狼籍。', '1', '2019-03-14 18:02:16');
INSERT INTO `video_drama_info` VALUES ('2', '9393904', '4289463', '拜见宫主大人第一季第2集', '硝烟散去，白眉大侠看似威慑武林的绝招却伤不了秦斩分毫，反倒是秦斩变化出一只巨大的黑色魔龙吓退了众人，保住了灵鹫宫。听闻手下的诉说，秦斩才知道自己已经昏睡了七日，其他帮派毁掉了他们之前的据点，走投无路，侍卫团的成员们才将他转移到了最后的地方灵鹫宫。没想到，穿越到游戏中的不仅仅是秦斩，还有他穿越之前在游戏中遇到的女孩雨晨，竟然和他一同穿越到了这游戏之中。', '2', '2019-03-14 18:02:18');
INSERT INTO `video_drama_info` VALUES ('3', '9393904', '4289483', '拜见宫主大人第一季第3集', '星宿派的丁老怪研制出了一种叫做春秋散的毒药，一但中此毒，身体会变得如磐石一般坚硬，但是却十分的痛苦，可谓是求生不得，求死不能。秦斩带领自己的侍卫团闯入了西夏皇族的皇宫内，仅凭三人之力，马上就要打到皇宫内部。西夏皇族太后派出大将军白冥启作出阻拦，本以为这西夏大将军无人能敌，却被秦斩手下侍卫团喵三轻松的应对。', '3', '2019-03-14 18:02:19');
INSERT INTO `video_drama_info` VALUES ('4', '9393904', '4289510', '拜见宫主大人第一季第4集', '秦斩众人被带到了一座大殿之中，侍卫团全部都中了丁老怪的春秋散。拖出昏昏欲睡的秦斩，丁老怪戳中了秦斩的死穴，正常人应该吐血，但是秦斩吐出的却都是喝下的毒酒。看见了一旁晕倒在地的手下们，秦斩感觉到了事情的危机。大殿的高台之上，坐着一位身穿黑色斗篷的蒙面人，秦斩向他索取解药，可是黑衣蒙面人一旁站立的女侍卫却告诉秦斩，如果想要换回解药，必须拿来灵鹫宫的宝丹作为交换。', '4', '2019-03-14 18:02:21');
INSERT INTO `video_drama_info` VALUES ('5', '9393904', '4289528', '拜见宫主大人第一季第5集', '薛神医出手，药到病除，侍卫团成员所中的毒已经被解开，这也让秦斩心里悬着的一块儿大石头落了地，唯一让他遗憾的是，就是薛神医无法医治好寒依的眼睛。雨晨和秦斩就此别过，三日之后开封城外的决战二人可不能忘，在武林同道众人面前说是决战，而在二人之间，只不过是他们独自见面的机会罢了。事已至此，秦斩已经看透，既然好人当不成，那就做坏人做到底，将邪派统一，与武林名门正派对抗到底。', '5', '2019-03-14 18:02:23');
INSERT INTO `video_drama_info` VALUES ('6', '9393904', '4304858', '拜见宫主大人第一季第6集', '幽深密林，深夜之战。赵顼身边的四大高手决战丁老怪。金刚腿四长老，铁头功大长老，金钟罩高长老和擒拿手手长老，四位长老联手，齐心合力共同应对丁老怪，拳脚比试，他们想要用大长老的铁头功击退丁老怪，一斗四，武林中赫赫有名的四大高手却不是丁老怪一人的对手，大战百回合后，尘土飞扬，丁老怪竟然解决掉了四大高手。', '6', '2019-03-14 18:02:24');
INSERT INTO `video_drama_info` VALUES ('7', '9393904', '4304867', '拜见宫主大人第一季第7集', '白眉大侠发现了秦斩，内心的恐惧让他的五官彻底扭曲，仓皇地逃离，他赶紧将秦斩来到开封的事情报告肖奈何，肖奈何坦然笑之，以为秦斩已经是瓮中之鳖，自有办法对付。深夜的盟主府，雨晨也已经是坐立难安，她到现在也无法理解，秦斩葫芦里面到底卖的是什么药？内心的忐忑让她准备亲自会面秦斩，把一切事情都说清楚。', '7', '2019-03-14 18:02:26');
INSERT INTO `video_drama_info` VALUES ('8', '9393904', '4323351', '拜见宫主大人第一季第8集', '皇帝震怒，下令处斩秦斩。雨晨大为不满，皇帝老儿根本就不清楚事情的前因后果，如此草率的决定，又如何以法治国。为了秦斩的生死，雨晨手拿圣旨，准备上那大殿之上与皇帝老儿争论一番。赵顼一路拦截，雨晨的莽撞举动不但不会让皇帝改变主意，反而对她自己也是大大的不利，担心雨晨的安危，皇子赵顼独挡一面，答应雨晨替秦斩向皇帝求情。', '8', '2019-03-14 18:02:27');
INSERT INTO `video_drama_info` VALUES ('9', '9393904', '4323360', '拜见宫主大人第一季第9集', '雨晨被人陷害，朝庭官兵突然来到雨晨的住处，在雨晨家中找到了她私通辽国的信件。赵顼，雨晨以及丐帮徐帮主，三人在路上发现了被绑在树上刚刚断气不久的耿直，白眉大侠的小徒弟。在耿直的身上，他们还发现了一块儿令牌，徐帮主见多识广，一眼便看出了这是桃花镇醉花斋的令牌。由此可想，白眉大侠一定在桃花镇。', '9', '2019-03-14 18:02:29');
INSERT INTO `video_drama_info` VALUES ('10', '9393904', '4347917', '拜见宫主大人第一季第10集', '清雪，喵三从河里救起重伤的徐帮主，却遭大辽猛士追杀，双双坠落悬崖。雨晨被萧奈何关入残剑山庄，百般审问，雨晨却对还魂丹之事一无所知，包括秦斩留给她保管的大包袋也空无一物。萧奈何疑心重重，决定召开武林论剑大会，公开处决雨晨，一为统治武林，二想逼其说出还魂丹下落。赵顼利用皇子身份来残剑山庄抢人，惨遭碰壁，在小冰的启发下，转而赶往灵鹫宫寻求帮助。', '10', '2019-03-14 18:02:31');
INSERT INTO `video_drama_info` VALUES ('11', '9393904', '4347932', '拜见宫主大人第一季第11集', '舒尔前往残剑山庄打探消息，被萧奈何发现后，施计逃走。赵顼为给雨晨洗清罪名，带人寻找清雪、喵三、徐帮主三人的下落。山崖下，喵三、清雪、徐帮主没死，醒来，大辽猛士袭来，三人同生共死一起击败了大辽猛士。秦斩通关了花楼，击败东方姐妹。明月也打通狗洞，与秦斩会合。灵鹫宫中手下们商量救雨晨的对策。论剑广场上，萧奈何逼迫雨晨交出还魂丹。赵顼以命相胁逼迫刘将军调兵。寒依、舒尔赶到论剑广场救雨晨，雨晨看出萧奈何覆灭武林的阴谋，劝舒尔等人离开，舒尔等人不听。', '11', '2019-03-14 18:02:33');
INSERT INTO `video_drama_info` VALUES ('12', '9393904', '4364609', '拜见宫主大人第一季第12集', '论剑广场上，正邪大战正式开始。雨晨大声呼喊众人住手，却没人听见她说的。清雪、喵三带着徐帮主赶到论剑广场，澄清误会，还雨晨清白。萧奈何见阴谋败漏，唤出大辽猛士，准备将武林人士一网打尽。明月看出塔主破绽趁秦斩与塔主相斗之际，用膏药封住了塔主的头顶排气的戒疤，塔主承受内力过多爆体而亡。', '12', '2019-03-14 18:02:35');
INSERT INTO `video_drama_info` VALUES ('13', '9393904', '4364618', '拜见宫主大人第一季第13集', '武林各派握手言和，秦斩在开封盟主府对面，挂牌盟主男人府居住，此举不仅令赵顼气恼，清雪也暗暗吃醋。清雪吵着让秦斩带她去山洞寻宝，秦斩不愿整天都陪着清雪，说自己有终身大事要办，也让清雪早点找如意郎君嫁了。清雪说自己要找像秦斩这样的，让秦斩吓了一大跳。秦斩与赵顼开始争夺雨晨。', '13', '2019-03-14 18:02:36');
INSERT INTO `video_drama_info` VALUES ('14', '9393904', '4381042', '拜见宫主大人第一季第14集', '秦斩带领手下们继续垦荒，雨晨带来解暑汤慰问大家。临走前秦斩给了雨晨一封信，雨晨也给了秦斩一封信。雨晨在信中对秦斩表露爱意，秦斩十分开心。清雪给秦斩送来冰镇果盘，得知雨晨给秦斩送了解暑汤，心里很不是滋味。秦斩开垦的荒地上开始绿草青青。秦斩让手下去买菜，准备宴会招待雨辰。秦斩等待雨晨赴宴，明月来报雨晨被赵顼用圣旨接去了皇宫，秦斩气急败坏。赵顼为雨晨设下豪华晚宴，吹笛子、放孔明灯以博取雨晨芳心。', '14', '2019-03-14 18:02:38');
INSERT INTO `video_drama_info` VALUES ('15', '9393904', '4381051', '拜见宫主大人第一季第15集', '秦斩和雨晨要结婚的消息传遍武林。清雪心乱如麻，借酒消愁，向丁老怪倾诉苦水。盟主府内，正派人士不解雨晨为何要下嫁给魔头秦斩。盟主男人府大厅，明月舒尔听闻秦斩要结婚，抱头痛哭。小河边，清雪和赵顼相互倾诉，赵顼两杯酒下肚就醉倒了。白冥启带领西夏侍卫团来寻清雪，清雪得知西夏有难，决定立即返回西夏，临走前，买了一个金簪送给秦斩。', '15', '2019-03-14 18:02:40');
INSERT INTO `video_drama_info` VALUES ('16', '9393904', '4395844', '拜见宫主大人第一季第16集', '明月在树林中醒来，想到寒依等人已经被控制，知道笑天真要对秦斩不利，却无法施展传音大法。笑天真假借送礼之名，封了雨晨穴道，将雨晨带到断心崖。秦斩赶到准备去救被吊在树上的雨晨，被笑天真阻止，笑天真讲述了自己这些年对秦斩恨意的由来，并说出已经封了雨晨全身的死穴，不久雨晨将会身亡命殒。秦斩打了笑天真几掌，冲上去救下雨晨，为雨晨传功解开一道道死穴。', '16', '2019-03-14 18:02:42');
INSERT INTO `video_drama_info` VALUES ('17', '9393904', '4395857', '拜见宫主大人第一季第17集', '雨晨思念秦斩，纵身跳下悬崖，四处寻找秦斩。笑天真以大辽国师的身份将耶律洪基当作傀儡皇帝，亲自封将点兵，四处征伐。大宋节节败退，雨晨所带的武林人士难以抵抗辽国铁骑。笑天真放出秦斩行踪的消息，让雨晨上钩。秦斩昏迷两个月，醒来发现自己在玲珑棋局经脉尽断丧失所有内力，想要加紧练习，恢复功力，却看到雨晨已死的消息，伤心颓废，饮酒度日。', '17', '2019-03-14 18:02:44');
INSERT INTO `video_drama_info` VALUES ('18', '9393904', '4412884', '拜见宫主大人第一季第18集', '秦斩虽然丧失了所有内力，但是脑子还在，记忆还在，他一手扫把，一手酒瓶，用一招醉酒的独孤剑法打倒了几名辽军。扫把终究是扫把，根本没有任何杀伤力，秦斩最终还是被辽兵一脚踢进了房间内，躲在房间椅子下瑟瑟发抖的潭台子羽冒出了头，拿出了一把他师傅曾经用来镇宅的宝刀递给了秦斩，秦斩利用这把宝刀击退了辽兵，救下了明月。', '18', '2019-03-14 18:02:46');
INSERT INTO `video_drama_info` VALUES ('19', '9393904', '4412893', '拜见宫主大人第一季第19集', '明月和丁老怪正在帮助舒尔找回沉睡的意识，与此同时喵三也对清雪展开了猛烈的攻击，一番打斗后，清雪误以为喵三恢复清醒，放下了戒备，于是喵三顺势制服清雪，几记重拳打下来，没想到喵三控制住了自己的杀心，随即意识恢复，舒尔难敌明月和丁老怪的回忆攻势也找回意识。秦斩一直在寒依身后，寒依感到有人尾随于是转身攻击秦斩，但在秦斩的温柔攻势下，寒依也摆脱了控制。', '19', '2019-03-14 18:02:49');
INSERT INTO `video_drama_info` VALUES ('20', '9393904', '4430671', '拜见宫主大人第一季第20集', '秦斩与雨晨重逢相拥。秦斩让雨晨休息，自己对付笑天真。秦斩使出独孤九刀，直接朝笑天真砍了过去。清雪、喵三带出杨立地，通过乾坤挪移功将巴掌和毒药都转移到笑天真的身上。笑天真突然冲破了杨立地的能力连接，杨立地只能用另一个办法，将所有人的内力传送给秦斩。', '20', '2019-03-14 18:02:51');

